"""# Fitting Alignment

We will now modify our occurence to perform a fitting alignment with affine gap penalties.

\begin{equation}
s^\rightarrow[i,j] = \max
\begin{cases}
0, & \mbox{if i = 0,} \\
s^\rightarrow[i,j-1] - \sigma, & \mbox{if j > 1,} \\
s^\searrow[i,j-1] - (\sigma + \rho), & \mbox{if j > 0,}
\end{cases}
\end{equation}

\begin{equation}
s^\searrow[i,j] = \max
\begin{cases}
0, & \mbox{if i = 0,} \\
s^\rightarrow[i,j], & \mbox{if j > 0,} \\
s^\downarrow[i,j], & \mbox{if i > 0,} \\
s^\searrow[i-1,j-1] - \delta(v_i,w_i), & \mbox{if i > 0 and j > 0,}
\end{cases}
\end{equation}

\begin{equation}
s^\downarrow[i,j] = \max
\begin{cases}
0, & \mbox{if i = 0,} \\
s^\downarrow[i-1,j] - \sigma, & \mbox{if i > 1,} \\
s^\searrow[i-1,j] - (\sigma + \rho), & \mbox{if i > 0.}
\end{cases}
\end{equation}

\begin{equation}
s^* = \max_j\{{\max\{s^\rightarrow[len(v),j], s^\searrow[len(v),j], s^\downarrow[len(v),j]\}}\}
\end{equation}
"""

ORIGIN = (0, 0, 0)
UP = (-1, 0, 0)
LEFT = (0, -1, 0)
TOPLEFT = (-1, -1, 0)
DELOPEN = (-1, 0, -1)
DELCLOSE = (0, 0, 1)
INSOPEN = (0, -1, 1)
INSCLOSE = (0, 0, -1)


def traceback_fitting(v, w, init_j, init_k, pointers):
    i, j, k = len(v), init_j, init_k
    new_v, new_w = [], []
    while True:
        if pointers[i][j][k] == None:
            raise TypeError
        di, dj, dk = pointers[i][j][k]

        if (di,dj,dk) == LEFT or (di,dj,dk) == INSOPEN:
            new_v.append('-')
            new_w.append(w[j-1])
        elif (di,dj,dk) == UP or (di,dj,dk) == DELOPEN:
            new_v.append(v[i-1])
            new_w.append('-')
        elif (di,dj,dk) == TOPLEFT:
            new_v.append(v[i-1])
            new_w.append(w[j-1])
            
        i, j, k = i + di, j + dj, k + dk
        if (i <= 0):
            break

    return ''.join(new_v[::-1]) + '\n' + ''.join(new_w[::-1])


def fitting_align(v, w, delta, sigma, rho):
    """
    Returns the score of the maximum scoring alignment of the strings v and w, as well as the actual alignment as 
    computed by traceback_global. 
    
    :param: v
    :param: w
    :param: delta
    :param: sigma
    :param: rho
    """

    M_h = [[0 for j in range(len(w)+1)] for i in range(len(v)+1)]
    M_d = [[0 for j in range(len(w)+1)] for i in range(len(v)+1)]
    M_v = [[0 for j in range(len(w)+1)] for i in range(len(v)+1)]

    pointers = [[[ORIGIN for k in range(3)] for j in range(len(w)+1)] for i in range(len(v)+1)]
    NULL = (float('-inf'), None)

    for i in range(len(v)+1):
        for j in range(len(w)+1):

            # Horizontal Table
            orig = (0, ORIGIN) if i == 0 else NULL
            extend_gap = (M_h[i][j-1] - sigma, LEFT) if j > 1 else NULL
            open_gap = (M_d[i][j-1] - (sigma + rho), INSOPEN) if j > 0 else NULL
            M_h[i][j], pointers[i][j][0] = max(orig, extend_gap, open_gap, key=lambda x: x[0])

            # Vertical Table
            orig = (0, ORIGIN) if i == 0 else NULL
            extend_gap = (M_v[i-1][j] - sigma, UP) if i > 1 else NULL
            open_gap = (M_d[i-1][j] - (sigma + rho), DELOPEN) if i > 0 else NULL
            M_v[i][j], pointers[i][j][2] = max(orig, extend_gap, open_gap, key=lambda x: x[0])

            # Diagonal Table
            orig = (0, ORIGIN) if i == 0 else NULL
            ins_close = (M_h[i][j], INSCLOSE) if j > 0 else NULL
            del_close = (M_v[i][j], DELCLOSE) if i > 0 else NULL
            sub = (M_d[i-1][j-1] + delta[v[i-1]][w[j-1]], TOPLEFT) if i > 0 and j > 0 else NULL
            M_d[i][j], pointers[i][j][1] = max(orig, ins_close, del_close, sub, key=lambda x: x[0])

    score = float('-inf')
    for j in range(len(w), -1, -1):
        if M_h[-1][j] >= score:
            score, init_j, init_k = M_h[-1][j], j, 0
        if M_d[-1][j] >= score:
            score, init_j, init_k = M_d[-1][j], j, 1
        if M_v[-1][j] >= score:
            score, init_j, init_k = M_v[-1][j], j, 2

    alignment = traceback_fitting(v, w, init_j, init_k, pointers)
    return score, alignment

"""Next we will test our fitting alignment algorithm by matching a substring of Candidatus Accumulibacter sp. SK-11 to Candidatus Accumulibacter adjunctus."""

score, alignment = fitting_align(s1, s2[50:150], blosum, sigma=1, rho=10)
print(f'Score:{score}\n{alignment}')

"""# Local Alignment

Finally, we will modify our occurence one last time to perform local alignments with affine gap penalties.

\begin{equation}
s^\rightarrow[i,j] = \max
\begin{cases}
0, \\
s^\rightarrow[i,j-1] - \sigma, & \mbox{if j > 1,} \\
s^\searrow[i,j-1] - (\sigma + \rho), & \mbox{if j > 0,}
\end{cases}
\end{equation}

\begin{equation}
s^\searrow[i,j] = \max
\begin{cases}
0, \\
s^\rightarrow[i,j], & \mbox{if j > 0,} \\
s^\downarrow[i,j], & \mbox{if i > 0,} \\
s^\searrow[i-1,j-1] - \delta(v_i,w_i), & \mbox{if i > 0 and j > 0,}
\end{cases}
\end{equation}

\begin{equation}
s^\downarrow[i,j] = \max
\begin{cases}
0, \\
s^\downarrow[i-1,j] - \sigma, & \mbox{if i > 1,} \\
s^\searrow[i-1,j] - (\sigma + \rho), & \mbox{if i > 0.}
\end{cases}
\end{equation}

\begin{equation}
s^* = \max_{i,j}\{{\max\{s^\rightarrow[i,j], s^\searrow[i,j], s^\downarrow[i,j]\}}\}
\end{equation}
"""

ORIGIN = (0, 0, 0)
UP = (-1, 0, 0)
LEFT = (0, -1, 0)
TOPLEFT = (-1, -1, 0)
DELOPEN = (-1, 0, -1)
DELCLOSE = (0, 0, 1)
INSOPEN = (0, -1, 1)
INSCLOSE = (0, 0, -1)


def traceback_local(v, w, init_i, init_j, init_k, pointers):
    i, j, k = init_i, init_j, init_k
    new_v, new_w = [], []
    while True:
        if pointers[i][j][k] == None:
            raise TypeError
        di, dj, dk = pointers[i][j][k]

        if (di,dj,dk) == LEFT or (di,dj,dk) == INSOPEN:
            new_v.append('-')
            new_w.append(w[j-1])
        elif (di,dj,dk) == UP or (di,dj,dk) == DELOPEN:
            new_v.append(v[i-1])
            new_w.append('-')
        elif (di,dj,dk) == TOPLEFT:
            new_v.append(v[i-1])
            new_w.append(w[j-1])
            
        i, j, k = i + di, j + dj, k + dk
        if pointers[i][j][k] == ORIGIN:
            break

    return ''.join(new_v[::-1]) + '\n' + ''.join(new_w[::-1])


def fitting_local(v, w, delta, sigma, rho):
    """
    Returns the score of the maximum scoring alignment of the strings v and w, as well as the actual alignment as 
    computed by traceback_global. 
    
    :param: v
    :param: w
    :param: delta
    :param: sigma
    :param: rho
    """

    M_h = [[0 for j in range(len(w)+1)] for i in range(len(v)+1)]
    M_d = [[0 for j in range(len(w)+1)] for i in range(len(v)+1)]
    M_v = [[0 for j in range(len(w)+1)] for i in range(len(v)+1)]

    pointers = [[[ORIGIN for k in range(3)] for j in range(len(w)+1)] for i in range(len(v)+1)]
    NULL = (float('-inf'), None)

    for i in range(len(v)+1):
        for j in range(len(w)+1):

            # Horizontal Table
            orig = (0, ORIGIN)
            extend_gap = (M_h[i][j-1] - sigma, LEFT) if j > 1 else NULL
            open_gap = (M_d[i][j-1] - (sigma + rho), INSOPEN) if j > 0 else NULL
            M_h[i][j], pointers[i][j][0] = max(orig, extend_gap, open_gap, key=lambda x: x[0])

            # Vertical Table
            orig = (0, ORIGIN)
            extend_gap = (M_v[i-1][j] - sigma, UP) if i > 1 else NULL
            open_gap = (M_d[i-1][j] - (sigma + rho), DELOPEN) if i > 0 else NULL
            M_v[i][j], pointers[i][j][2] = max(orig, extend_gap, open_gap, key=lambda x: x[0])

            # Diagonal Table
            orig = (0, ORIGIN)
            ins_close = (M_h[i][j], INSCLOSE) if j > 0 else NULL
            del_close = (M_v[i][j], DELCLOSE) if i > 0 else NULL
            sub = (M_d[i-1][j-1] + delta[v[i-1]][w[j-1]], TOPLEFT) if i > 0 and j > 0 else NULL
            M_d[i][j], pointers[i][j][1] = max(orig, ins_close, del_close, sub, key=lambda x: x[0])

    score = float('-inf')
    for i in range(len(v), -1, -1):
        for j in range(len(w), -1, -1):
            if M_h[i][j] >= score:
                score, init_i, init_j, init_k = M_h[i][j], i, j, 0
            if M_d[i][j] >= score:
                score, init_i, init_j, init_k = M_d[i][j], i, j, 1
            if M_v[i][j] >= score:
                score, init_i, init_j, init_k = M_v[i][j], i, j, 2
            
    alignment = traceback_local(v, w, init_i, init_j, init_k, pointers)
    return score, alignment

